#include <iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main(){
    int n,m;
    int ans=0;
    cin>>n>>m;
    vector<int>a(n);
    for(int i=0;i<n;i++){
        cin>>a[i];
    }
    for(int i=0;i<n;i++){
        if((i+1)*a[i]<=m){
            ans+=i+1;
            m-=a[i]*(i+1);
            }else{
                ans+=a[i]/m;
            }
            }
            std::cout<<ans<<std::endl;
}
