#include<iostream>
#include<algorithm>

int main() {
	int n;
	std::cin >> n;
	int a[100000];
	int b[100000];
	for (int i = 0; i < n; i++) {
		std::cin >> a[i];
		std::cin >> b[i];
		
	}
	std::sort(a, a + n - 1);
	std::sort(b, b + n);
	int ans = 0;
	for (int i = 0; i < n; i++) {
		ans += a[i] * b[n - i - 1];
	}
	std::cout << ans << std::endl;
}
