#include<iostream>
#include<string>
#include<vector>

int main() {
	int N, M;
	std::cin >> N >> M;

	int ban[100000000];
	std::vector<int>lunk;
	for (int i = 0; i < N; i++) {
		std::cin >> ban[i];
		for (int j = 0; j < lunk.size(); j++) {
			if (lunk[j] > ban[i])lunk.insert(lunk.begin() + j, i);
		}
	}

	int k = 0;
	int z = 0;
	while (1) {
		for (int i = 0; i < lunk[z]; i++) {
			M -= ban[lunk[z]];
			if (M >= 0)k++;
			else goto flag;
		}
		z++;
	}
flag:;
	std::cout << k << std::endl;

	return 0;
}
