#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>

int main(int argc, char **argv)
{
	unsigned long long x, ans=1;
	std::cin >> x;
	for(int i = 0; i < x; i++){
		ans *= 2;
		ans %= 1000000007;
	}
	std::cout << ans - 1 << std::endl;
	return 0;
}
