#include <iostream>
#include <vector>
#include <cmath>
int main(){
    int n,m;
    std::cin>>n>>m;
    std::vector<int> a(m),b(m),c(m);
    for(int i=0;i<m;++i){
        std::cin>>a[i]>>b[i];
    }
    int x=ceil(n/a[0])*b[0];
    for(int i=1;i<m;++i){
        c[i]=ceil(n/a[i])*b[i];
        if(x>c[i]){
            x=c[i];  
        }
    }
    std::cout<<x<<std::endl;
}
