#include<iostream>
int main() {
	int n = 0, m = 0, c = 0, a[10000], b[10000], d = 0,e[10000];
	std::cin >> n>> m;
	for (int i = 0; i < m; i++) {
		std::cin >> a[i]>> b[i];
	}
	c = n/a[0]*b[0];
	if (n%a[0] != 0) { c = c + b[0]; }
	for (int i = 0; i < m; i++) {
		e[i] = n / a[i] * b[i];
		if (n%a[i] != 0) { c = c + b[i]; }
		}
	for (int i = 0; i < m; i++) {
		if (c>=e[i]) {
			c = e[i];
			d = e[i];
		}
	}
	std::cout << d << std::endl;
}
