#include <iostream>
#include <vector>
int main() {
	int peo, sto;
	std::vector <int> vec;
	std::vector <int> vec2;
	std::cin >> peo >> sto;
	int min = 1000000;
	for (int i = 0; i < sto; ++i) {
		int x, y;
		std::cin >> x >> y;
		vec.push_back(x), vec2.push_back(y);
	}
	for (int i = 0; i < sto; ++i) {
		int x;
		if (peo % vec[i] == 0)
			x = peo / vec[i];
		else if (peo % vec[i] != 0)
			x = peo / vec[i] + 1;
		if (min >= x * vec2[i])
			min = x * vec2[i];
	}
	std::cout << min << std::endl;

	system("pause");
}
