#include <iostream>
#include <string>
using namespace std;
int main() {
	int need = 0;
	int gyudon = 0;
	int kosuu[10001] = {};
	int Yen[10001] = {};
	int needmoney[10001] = {};
	int answer = 0;
	cin >> need >> gyudon;
	for (int i = 1; i <= gyudon; i++) {
		cin >> kosuu[i] >> Yen[i];
		if (need%kosuu[i] == 0) {
			needmoney[i] = Yen[i] * need / kosuu[i];
		}
		else {
			needmoney[i] = Yen[i] * (need / kosuu[i] + 1);
		}
		if (i != 1) {
			if (needmoney[i] > needmoney[i - 1]) {
				answer = needmoney[i - 1];
			}
			if (needmoney[i] < needmoney[i - 1]) {
				answer = needmoney[i];
			}
		}
		else {
			answer = needmoney[1];
		}
	}
	cout << answer << '\n';
}
