#include <iostream>
int main(){
	int N,M,A[10000]={},B[10000]={},cosp=10000,cosp1=0;
	std::cin>>N>>M;
	for(int i=0;i<M;i++){
		std::cin>>A[i];
		std::cin>>B[i];
	}
	for(int i=0;i<M;i++){
		if(B[i]/A[i]<=cosp){
			cosp=B[i]/A[i];
			cosp1=i;
		}
	}
	for(int i=1;i<=N;i++){
		if(A[cosp1]*i>=N){
			std::cout<<B[cosp1]*i<<std::endl;
			break;
		}
	}
	return 0;
}
