#include<iostream>
#include<string>
#include<cmath>

std::string str;

int main(){
    
    if(std::getline(std::cin, str)){
        for(int i=0;i<sizeof(str);i++){
            if(str[i]>'a'&&str[i]<'z')
                str[i]-=1;
            else
            if(str[i]=='a')
                str[i]='z';
        }
    }
    
    std::cout<<str<<std::endl;
    
    return 0;
    
}
