#include <utility>
#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <map>
#include <cmath>
#include <numeric>
#include <limits>
#define ll long long
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")<<std::endl
#define YES(b) std::cout<< (b ? "YES" : "NO")<<std::endl
#define fr first
#define se second
using namespace std;

int main() {
	int bc = 0 , wc = 0;
	bool b = false , w = false;
	int sb = 0 , sw = 0;;
	string bb = {}, ww = {};
	string s;
	cin >> s;
	rep(i, s.size())
	{
		if (s[i] == 'B') {
			if (!b) {
				b = true;
			}
			else {
				if (sw > sb&&sw < i) {
					w = false;
				}
				replace(s.begin()+sb, s.begin()+i, 'W', 'B');
		
			}
			sb = i;
			bb = {};
		}
		else if (s[i] == 'W') {
			if (!w) {
				w = true;
			}
			else {
				if (sb > sw&&sb < i) {
					b = false;
				}
				
				replace(s.begin()+sw, s.begin()+i, 'B', 'W');
				
			}
			sw = i;
			ww = {};
		}
		if (s[i] == 'E') {
			b = false;
			w = false;
		}
	}
	rep(i, s.size()) {
		if (s[i] == 'B') {
			bc++;
		}
		if (s[i] == 'W') {
			wc++;
		}
	}
	if (bc < wc) {
		cout << "White wins" << endl;
	}
	else if (bc > wc) {
		cout << "Black wins" << endl;
	}
	else {
		cout << "Draw";
	}
	return 0;
}
