#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

 int main (){

	 ULL x;
	 std::cin >> x;

	ULL sum = 1;

	 for (ULL i = 0; i < x; ++i) {
		 sum *= 2;
	 }

	 std::cout << (sum - 1) % 1000000007 << std::endl;
	 return 0;
}
