#include <iostream>
int main(){
	int N,M,cosp=1000000,cosp1=0;
	std::cin>>N>>M;
	int A[10000]={},B[10000]={};
	for(int i=0;i<M;i++){
		std::cin>>A[i];
		std::cin>>B[i];
	}
	for(int i=0;i<M;i++){
		if(B[i]*100/A[i]*100<=cosp){
			cosp=B[i]*100/A[i]*100;
			cosp1=i;
		}
	}
	for(int i=1;i<=N;i++){
		if(A[cosp1]*i>=N){
			std::cout<<B[cosp1]*i<<std::endl;
			break;
		}
	}
	return 0;
}
