#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>

int main(int argc, char **argv)
{
	int n, m, ope, s, t, v, k;
	std::cin >> n >> m;
	std::vector<int> vec(n,0);
	for(int i = 0; i <= m; i++){
		std::cin >> ope;
		if(ope==1){
			std::cin >> s >> t >> v;
			for(int j = s; j < t; j++){
				vec[j] = v;
			}
		}
		if(ope==2){
			std::cin >> k;
			std::cout << vec[k] << std::endl;
		}
	}
	return 0;
}
