#define rep(i,rept) for (int i = 0; i < rept; i++)
typedef long long ll;

int main() {
	int n = 0, m = 0;
	int count = 0;
	int a[10000] = {}; 
	int b[10000] = {};
	int f[10000] = {};
	int money[10000] = {};
	std::cin >> n >> m;
	rep(i, m) {
		std::cin >> a[i] >> b[i];
	}
	
	for (int i = 0; i < m; i++) {
		f[i] = n / a[i];
		if (n%a[i] == 0) {
			money[i] = b[i] * f[i];
		}
		else {
			money[i] = b[i] * (f[i]+1);
		}
	}
	std::sort(money, money + m);
	std::cout << money[0] << std::endl;
	return 0;
}
