#include<iostream>
int main() {
	int n, m, a[10000], b[10000], A[10000];
	int long long B = 0;
	std::cin >> n >> m;
	for (int i = 0;i<m;i++) {
	std::cin >> a[i] >> b[i];
		if (n%a[i] == 0)
			A[i] = n / a[i] * b[i];
		else
			A[i] = (n / a[i])*b[i] + b[i];
	}
	if (m == 1)
		std::cout << A[0] << std::endl;
	else {
		B = A[0];
		for (int i = 0; i < m-1 ; i++) {
			if (B >= A[i + 1])
				B = A[i+1];
		}
		std::cout << B << std::endl;
	}
	return 0;
}
