#include <iostream>
#include <vector>
#include <cmath>
int main(){
    int n,m,cnt=0;
    std::cin>>n>>m;
    std::vector<int> a(n);
    for(int i=0;i<n;++i){
        std::cin>>a[i];
    }
    for(int i=0;i<n;++i){
        for(int j=0;j<=i;++j){
            if(m>a[i]){
                m=m-a[i];
                ++cnt;
            }
        }
    }
    std::cout<<cnt<<std::endl;
}
