#include <iostream>
#include <vector>

int main() {
	int n, m;
	std::cin >> n >> m;
	std::vector<int> a, b;
	for (int i = 0; i < m; i++) {
		int aa, bb;
		std::cin >> aa >> bb;
		a.push_back(aa);
		b.push_back(bb);
	}
	int minyen = 99999999999;
	for (int i = 0; i < m; i++) {
		int kosuu = n / int(a.at(i));
		int amari = n % int(a.at(i));
		if (amari != 0) {
			kosuu++;
		}
		int ryokin = kosuu * int(b.at(i));
		if (ryokin < minyen) {
			minyen = ryokin;
		}
	}
	std::cout << minyen << std::endl;
}
