#include<iostream>
#include<string>
#include<vector>

int main() {
	int N, M;
	std::cin >> N >> M;

	int ban[60000];
	std::vector<int>lunk;
	for (int i = 0; i < N; i++) {
		std::cin >> ban[i];//ban[0]=10;//ban[1]=7;//ban[0]=19;
		if(lunk.size() == 0)lunk.push_back(i);
		else {
			for (int j = 0; j < lunk.size(); j++) {
				if (ban[lunk[j]] > ban[i]) {
					lunk.insert(lunk.begin() + j, i);
					break;
				}
				else if (j == lunk.size() - 1) {
					lunk.push_back(i);
					break;
				}
			}
		};
	}

	int k = 0;
	int z = 0;
	while (M >= 0) {
		for (int i = 0; i < lunk[z] + 1; i++) {
			M -= ban[lunk[z]];
			if (M >= 0)k++;
		}
		z++;
	}
	std::cout << k << std::endl;
	return 0;
}
