#include<iostream>
#include<cmath>
#include<algorithm>
#include <cassert>
using namespace std;
int main() {
	int N, M, A[10000], B[10000], fee[10000], total1, total2,a[10000];
	cin >> N >> M;
	for (int p = 0; p < M ; p++) {
		cin >> A[p];
		cin >> B[p];
	}
	for (int j = 1; j < M + 1; j++) {
		
		if (N%B[j - 1] > 0) {
			a[j - 1] = N / B[j - 1] + 1;
		}
		else {
			a[j - 1] = N / B[j - 1];
		}
	}
	for (int q = 1; q < M+1 ; q++) {
		fee[q-1] =  a[q-1]*B[q-1];
	}

	total2 = min(fee[0],114514);
	if (M > 1) {
		for (int w = 1; w < M + 1; w++) {
			total2 = min(fee[w-1], fee[w ]);
			
		}
	}

	total1 = N * total2;
	cout << total1 << endl;
}
