#include "iostream"
#include "string"

using namespace std;

int main() {
	string s;
	getline(cin, s);
	bool flag = false;
	for (int i = 1; i < 26; i++) {
		for (int j = 0; j < s.size(); j++) {
			if (s[j] < 'a' || s[j] > 'z') continue;
			s[j]++;
			if (s[j] > 'z') s[j] -= 26;
		}
		for (int j = 0; j < s.size() - 5; j++) {
			if (s[j] == 'c' && s[j + 1] == 'a' && s[j + 2] == 'e' &&  s[j + 3] == 's' &&  s[j + 4] == 'a' &&  s[j + 5] == 'r') {
				flag = true;
			}
		}
		if (flag) break;
	}
	cout << s << endl;

	return 0;
}
