using namespace std;
typedef long long ll;
typedef unsigned long long ull;
const int N = 2000100;
const int mod = (int)pow(10, 9) + 7;
const ll INF = 1LL << 60;
#define rep(i,n) for(ll i=(ll)0;i<(ll)n;++i)
#define ALL(x) x.begin(),x.end()
#define pp pair<ll,ll>
#define ppp pair<ll,pp>
#define fi first
#define se second
#define pb push_back
#define fix(n) fixed<<setprecision(n)
#define itr vector<ll>::iterator
#define print(s) cout<<s<<endl
#define inp(s) cin>>s
ll h, w, n, m;
char mp[110][110];
bool fl = 0;
void dfs(pp now,ll t,ll ct2[110][110]) {
	//cout << now.first << "," << now.second << endl;
	ll x = now.fi, y = now.se;
	ll ct[110][110];
	rep(i, h)rep(j, w)ct[i][j] = ct2[i][j];
	if (ct[x][y] != -1) { fl = 1; return; }
	if (t == n) { fl = 1; return; }
	ct[x][y] = t;
	bool ffl = 0;
	if (x > 0 && mp[x - 1][y] == 'w'&& ct[x - 1][y]<=t+1-m) { dfs(pp{ x - 1,y }, t + 1, ct); ffl = 1; }
	if (x < h - 1 && mp[x + 1][y] == 'w'&& ct[x - 1][y] <= t + 1 - m) { dfs(pp{ x + 1,y }, t + 1, ct); ffl = 1; }
	if (y > 0 && mp[x][y - 1] == 'w'&&ct[x - 1][y] <= t + 1 - m) { dfs(pp{ x,y - 1 }, t + 1, ct); ffl = 1; }
	if (y < w - 1 && mp[x][y + 1] == 'w'&&ct[x - 1][y] <= t + 1 - m) { dfs(pp{ x,y + 1 }, t + 1, ct); ffl = 1; }
	if(!ffl)fl &= 0;
}
pp st;
int main() {
	ll ctt[110][110];
	cin >> h >> w >> n >> m;
	rep(i, h)rep(j, w) { 
		ctt[i][j] = -1;
		cin >> mp[i][j]; 
		if (mp[i][j] == 'S')st = pp{ i,j };
	}
	dfs(st, 0,ctt);
	cout << (fl ? "Yes" : "No") << endl;
	return 0;
}
