#include <iostream>
#include <string>
using namespace std;
int main() {
	unsigned long long int X;
	cin >> X;
	unsigned long long int x;
	unsigned long long int h;
	for (int i = 1; i <= X; i++) {
		x = 2 * x;
	}
	h = x - 1;
	unsigned long long int answer = 0;
	answer = h / 1000000007;
	cout << answer << '\n';
}
