#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

 int main (){

	 int n;

	 std::cin >> n;

	 int m;

	 std::cin >> m;

	 int a[10001];
	 int b[10001];
	 ULL sum = 1000000000;
	 for (int i = 0; i < m; ++i) {
		 std::cin >> a[i];
		 std::cin >> b[i];
	 }

	 for (int i = 0; i < m; ++i) {
		 if (a[i] % 2 == 0) {
			 if (sum > n / a[i] * b[i]) {
				 sum = n / a[i] * b[i];
			 }
		 }
		 else if (a[i] == 1) {
			 if (sum > n / a[i] * b[i]) {
				 sum = n / a[i] * b[i];
			 }
		 }
		 else {
			 if (sum > n / a[i] * b[i] + b[i]) {
				 sum = n / a[i] * b[i] + b[i];
			 }
		 }
	 }

	 std::cout << sum << std::endl;
	 return 0;
}
