#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <map>
#include <queue>
#include <stack>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;
int n, m, ans, A[101010], B[101010];
string s;
int f(int i, int j) {
	if (i%j == 0)
		return i / j;
	else
		return i / j + 1;
}
int main() {
	cin >> n >> m;
	vector<pair<int, int> > p(n);
	rep(i, n) {
		cin >> A[i];
		p[i].first = A[i];
		p[i].second = i + 1;
	}
	sort(p.begin(), p.end());
	rep(i, n) {
		if (p[i].first*p[i].second <= m) {
			m -= p[i].first*p[i].second;
			ans += p[i].second;
		}
		else {
			m -= p[i].first*f(m, p[i].first*p[i].second);
			ans += f(m, p[i].first*p[i].second);
		}
	}
	cout << ans << endl;
	return 0;
}
