#include <iostream>
#include <algorithm>
#include <string>
#include <vector>

using namespace std;

int main() {
	int n, m, w[10000] = {}, e[10000] = {}, r[10000] = {};
	cin >> n >> m;
	for (int i = 0;i < m;i++) {
		cin >> w[i] >> e[i];
	}
	for (int o = 0;o < m;o++) {
		r[o] = e[o] / w[o];
	}
	sort(r, r + m);
	cout << r[0]*n << endl;
}
