#include <iostream>
#include <string>
int main() {
	std::string str;
	char a = 97;
	char z = 122;
	int N;
	std::cin >> N;
	std::cin >> str;
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] >= 97 && str[i] <= 122) {
			str[i] -= N;
		}
		if (str[i] < 97)
			str[i] = 122 - str[i] + 96;
	}
	std::cout << str << std::endl;
	system("pause");
}
