#include <iostream>
#include <string>
#include <algorithm>
using namespace std;
	int main() {
		int n, m;
		int q[100];
		cin >> n >> m;
		for (int i = 0; i < m; i++) {
			int a, b;
			cin >> a >> b;
			int r= 0;
			//セット数の判定
			if (n / a == 0) {
				r = n / a;
					
			}

			else{
				r = n / a + 1;
			}
			q[i] = a * b*r;
		}

		int e = 100000;
		for (int i = 0; i < m-1; i++) {
			e = min({ q[i],q[i + 1] });
		
		}
		cout << e << endl;
	}
