#include <iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main(){
    int n,m;
    cin>>n>>m;
    vector<int>a(m);
    vector<int>b(m);
    vector<int>c(m);
    for(int i=0;i<m;i++){
        cin>>a[i]>>b[i];
    }
    for(int i=0;i<m;i++){
        if(m%a[i]==0){
            c[i]=(n/a[i])*b[i];
        }else{
            c[i]=((n/a[i])+1)*b[i];
        }
    }
    sort(c.begin(),c.end(),greater<>());
    int ans=c[m-1];
    cout<<ans<<endl;
}
