#include <iostream>
#include <vector>
#include <cmath>
#include <algorithm>
int main(){
    int n,m;
    std::cin>>n>>m;
    std::vector<int> a(m),b(m),c(m);
    for(int i=0;i<m;++i){
        std::cin>>a[i]>>b[i];
    }
    for(int i=0;i<m;++i){
        if(n%a[i]==0){
            c[i]=(n/a[i])*b[i];
        }
        else{
            c[i]=(n/a[i]+1)*b[i];
        }
    }
    std::sort(c.begin(),c.end());
    std::cout<<c[0]<<std::endl;
}
