#include <iostream>
#include <string>
using namespace std;
int main() {
	char ch[100000];
	int mojisuu = 0;
	int zure = 0;
	for (int i = 0; i <= 10000; i++) {
		while (cin.get(ch[i])) {//cin.get(?)で一文字ずつ代入する
			mojisuu++;
			if (ch[i] == ' ') {
				if (mojisuu == 6) {
					if ((ch[i - 5] - '0') - (ch[i - 4] - '0') == 2 &&
						(ch[i - 3] - '0') - (ch[i - 4] - '0') == 4 &&
						(ch[i - 2] - '0') - (ch[i - 3] - '0') == 14 &&
						(ch[i - 1] - '0') - (ch[i - 2] - '0') == 18 &&
						(ch[i] - '0') - (ch[i - 1] - '0') == 17 &&
						(ch[i - 5] - '0') + (ch[i - 4] - '0') +
						(ch[i - 3] - '0') + (ch[i - 2] - '0') +
						(ch[i - 1] - '0') + (ch[i] - '0') == 46) {
						zure = (ch[i - 5] - '0') - 3;
					}
				}
				mojisuu = 0;
			}
		}
	}
	for (int i = 0; i <= 10000; i++) {
		while (cin.get(ch[i])) {//cin.get(?)で一文字ずつ代入する
			if (ch[i] != ' ') {
				cout.put(ch[i] - zure);//cout.put(?)で一文字づつ出力する
			}
			else{
				cout.put(ch[i]);
			}
		}
	}
	cout << '\n';
}
