#include <iostream>
#include <string>
#include <algorithm>
using namespace std;
int main() {
	int n, m;
	int q[10000];
	cin >> n >> m;
	for (int i = 0; i < m; i++) {
		int a, b;
		cin >> a >> b;
		int r;
		//セット数の判定
		if (n / a == 0) {
			r = n / a;
		}

		else {
			r = n / a + 1;
		}
		q[i] = a * b*r;
	}

	int e=q[0];
	for (int i = 1; i <= m; i++) {
		e= min({ e,q[i] });

	}
	cout << e << endl;
}
