#include<iostream>
#include<string>
#include<vector>
#include<stdio.h>

char shift_char(int shift_count, char char_in)
{
	char str[27] = "abcdefghijklmnopqrstuvwxyz";
	int i, j;

	for (i = 0; i < 26; i++) {
		if (char_in == str[i]) break;
	}
	if (i >= 26) return char_in; /* この行を追加する */

	j = (i + shift_count) % 26;

	if (j < 0) {
		j += 26;
	}
	return str[j];
}

int main() {
	std::string str;
	std::getline(std::cin, str);

	while (str.find("caesar") == std::string::npos) {
		std::string test = str;
		for (int i = 0; i < test.size(); i++) {
			test[i] = shift_char(1, test[i]);
		}
		str = test;
	}
	std::cout << str << std::endl;
	return 0;
}
