#include <utility>
#include <iostream>
#include <vector>
#include <algorithm>
#include <string>
#include <map>
#include <cmath>
#include <numeric>
#include <limits>
#define ll long long
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")<<std::endl
#define YES(b) std::cout<< (b ? "YES" : "NO")<<std::endl
#define fr first
#define se second
using namespace std;

int main() {
	int n, m,k;
	vector<pair<int, int>> v = {};
	cin >> n >> m;
	rep(i, n) {
		cin >> k;
		v.push_back(make_pair(k, i + 1));
	}
	sort(v.begin(), v.end());
	k = 0;
	rep(i, n) {
		if (m < v[i].fr) {
			break;
		}
		k += min(m / v[i].fr, v[i].se);
		m -= v[i].fr*min(m / v[i].fr, v[i].se);
	}
	cout << k << endl;
	return 0;
}
