#include <iostream>
int main(){
	int N,a,b,M=0;
	std::cin>>N;
	int A[1001]={},B[1001]={};
	for(int i=0;i<N;i++){
		std::cin>>A[i];
		std::cin>>B[i];
	}
	for(int i=0;i<N-1;i++){
		if(A[i]<A[i-1]){
			a=A[i];
			A[i]=A[i-1];
			A[i-1]=a;
		}
	}
	for(int i=0;i<N-1;i++){
		if(B[i+1]>B[i]){
			b=B[i+1];
			B[i+1]=B[i];
			B[i]=b;
		}
	}
	for(int i=0;i<N;i++){
		M+=A[i]*B[i];
	}
	std::cout<<M<<std::endl;
	return 0;
}
