#include <iostream>
#include <vector>

int main() {
	int n, m;
	std::cin >> n >> m;
	std::vector<int> a, out;
	for (int i = 0; i < n; i++) {
		a.push_back(0);
	}
	for (int i = 0; i < m; i++) {
		int a1, b, c, d;
		std::cin >> a1 >> b;
		if (a1 == 2) {
			out.push_back(int(a.at(b)));
		}
		else if (a1 == 1) {
			std::cin >> c >> d;
			int saisho = b;
			for (int hh = 0; hh < (c - b + 1); hh++) {
				a.at(saisho + hh) = a.at(saisho + hh ) + d;
			}
		}
	}
	for (int i = 0; i<int(out.size()); i++) {
		std::cout << int(out.at(i)) << std::endl;
	}
}
