#include<iostream>
#include<vector>
#include<string>

int main() {
	int n;
	int m;
	std::cin >> n;
	std::cin >> m;
	int a[60000];
	int b[60000];
	int c;
	bool flug = true;
	for (int i = 0; i < n; i++) {
		std::cin >> a[i];
		b[i] = i+1;
	}
	while(flug){
		flug = false;
		for (int i = 0; i < n - 1; i++) {
			if (a[i] > a[i + 1]) {
				c = a[i];
				a[i] = a[i + 1];
				a[i + 1] = c;

				c = b[i];
				b[i] = b[i + 1];
				b[i + 1] = c;
				flug = true;
			}
		}
		for (int i = n-1; i < 0; i--) {
			if (a[i-1] > a[i]) {
				c = a[i-1];
				a[i-1] = a[i];
				a[i] = c;

				c = b[i-1];
				b[i-1] = b[i];
				b[i] = c;
				flug = true;
			}
		}
	}
	
	int ans = 0;
	for (int i = 0; i < n; i++) {
		if (m < a[i]) {
			break;
			
		}
		else{
			if (m / a[i] < b[i]) {
				ans += m / a[i];
				m -= a[i] * (m / a[i]);
			}
			else {
				ans += b[i];
				m -= a[i] * b[i];
			}
		}
	}
	std::cout << ans << std::endl;
}
