const ll INF = 1LL << 60;
#define rep(i,n) for(ll i=(ll)0;i<(ll)n;++i)
#define ALL(x) x.begin(),x.end()
#define pp pair<ll,ll>
#define ppp pair<ll,pp>
#define fi first
#define se second
#define pb push_back
#define fix(n) fixed<<setprecision(n)
#define itr vector<ll>::iterator
#define print(s) cout<<s<<endl
#define inp(s) cin>>s
ll dp[1000][1000], n, m, v[1010], c[1010];
int main() {
	cin >> n >> m;
	rep(i, n)cin >> v[i] >> c[i];
	rep(i, n)rep(j, m+1) {
		if (i == 0)dp[i][j] = (j / c[i])*v[i];
		else {
			if (j - c[i] >= 0)dp[i][j] = max(dp[i][j - c[i]] + v[i], dp[i - 1][j]);
			else dp[i][j] = max(dp[i][j-1],dp[i-1][j]);
		}
		//cout << i << " " << j << " " << dp[i][j] << endl;
	}
	cout << dp[n - 1][m] << endl;
	return 0;
}
