#include "iostream"
#include "string"
#include "vector"
#include "algorithm"

using namespace std;

struct p {
	long long num, cost;
};

int main() {
	vector<p> a, c;
	vector<long long> b;
	int n, m;
	cin >> n >> m;
	for (int i = 0; i < n; i++) {
		long long tmp;
		cin >> tmp;
		p p;
		p.cost = tmp;
		p.num = i;
		a.push_back(p);
		b.push_back(tmp);
	}
	sort(b.begin(), b.end());
	c = a;
	int ans = 0;
	for (int i = 0; i < n; i++) {
		for (int k = 0; k < n; k++) {
			if (b[i] == a[k].cost) {
				int count = 0;
				while (m > 0 && count < a[k].num) {
					m -= a[k].cost;
					count++;
					ans++;
				}
			}
		}
	}

	cout << ans + 1<< endl;

	return 0;
}
