#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>

char shift_char(int shift_count, char char_in)
{
	char str[27] = "abcdefghijklmnopqrstuvwxyz";
	int i, j;

	for (i = 0; i < 26; i++) {
		if (char_in == str[i]) break;
	}
	if (i >= 26) return char_in; /* この行を追加する */

	j = (i + shift_count) % 26;

	if (j < 0) {
		j += 26;
	}
	return str[j];
}

int main() {
	srand((unsigned)time(NULL));
	if (rand() % 2 == 0)std::cout << "Yes" << std::endl;
	else std::cout << "No" << std::endl;
	return 0;
}
