 #include<iostream>
 #include<algorithm>
 int H,W,N,M;
 int DP[110][110];
 char gr[110][110];
 int dx[]={0,1,0,-1};
 int dy[]={1,0,-1,0};
bool flag=false;

void dfs(int x,int y){
    if(DP[x][y]>=N){
        flag=true;
        return;
    }
    for(int i=0;i<4;i++){
        int rx=dx[i]+x;
        int ry=dy[i]+y;
        if(1<=rx&&rx<=H&&1<=ry&&ry<=W){
            if(DP[x][y]+1-DP[rx][ry]>=M&&gr[rx][ry]=='w'){
                int tmp=DP[rx][ry];
                DP[rx][ry]=DP[x][y]+1;
                dfs(rx,ry);
                DP[rx][ry]=tmp;
            }
        }
    }
}


 int main(){
     int sh,sw;
     std::cin>>H>>W>>N>>M;
     for(int i=1;i<=H;i++){
         for(int j=1;j<=W;j++){
            std::cin>>gr[i][j];
            if(gr[i][j]=='S'){
                sh=i;
                sw=j;
            }
         }
     }
     dfs(sh,sw);
     std::cout<<(flag?"Yes":"No")<<std::endl;
     return 0;
 }
