#include <iostream>
#include <climits>
#include <algorithm>
#define ll long long int
using namespace std;

// 入力
int n, W;
ll weight[1000], value[1000];

// DPテーブル
ll dp[1000][1000];

int main() {
	cin >> n >> W;
	for (int i = 0; i < n; ++i) cin >> value[i] >> weight[i];

	// DP初期条件: dp[0][w] = 0
	for (int w = 0; w <= W; ++w) dp[0][w] = 0;

	// DPループ
	for (int i = 0; i < n; ++i) {
		for (int w = 0; w <= W; ++w) {
			if (w >= weight[i]) dp[i + 1][w] = max(dp[i][w - weight[i]] + value[i], dp[i][w]);
			else dp[i + 1][w] = dp[i][w];
		}
	}

	cout << dp[n][W] << endl;
}
