#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <map>
#include <queue>
#include <stack>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;
int n, m, ans[101010101], A[10101010], B[10101010], C[10101010], D[10101010];
string s;
int main() {
	cin >> n >> m;
	rep(i, m) {
		cin >> A[i] >> B[i];
		if (A[i] == 1) {
			cin >> C[i] >> D[i];
			for (int j = B[i]; j < C[i]; j++) ans[j] = D[i];
		}
		else
			cout << ans[B[i]] << endl;
	}
	return 0;
}
