#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
using namespace std;
int main() {
	int n,m,w[10000],e[10000],r[10000],l[10000];
	cin >> n >> m;
	for (int i = 0;i < m;i++) {
		cin >> w[i] >> e[i];
	}
	for (int o = 0;o < m;o++) {
		if (n%w[o] != 0) {
			r[o] = n / w[o] + 1;
		}
		else {
			r[o] = n / w[o];
		}
	}
	for (int u = 0;u < m;u++) {
		l[u] = r[u] * e[u];
	}
	sort(l, l + m);
	cout << l[0] << endl;
}
