#include <iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main(){
    long long int a;
    cin>>a;
    long long int ans;
    for(int i=1;i<a;i++){
        ans*=2;
    }
    long long int ababa;
    if((ans-1)>1000000007){
        ababa=(ans-1)%1000000007;
    }else{
        ababa=ans-1;
    }
    cout<<ababa<<endl;
}
