#include <iostream>
#include <stdio.h>
#include <limits.h>

int main() {
	long long X;
	std::cin >> X;
	long long kakko = 2;
	for (long long i = 2; i <= X; i++) {
		kakko = kakko * 2;
	}
	kakko = kakko - 1;
	long long aaa = kakko % 1000000007;
	std::cout << aaa << std::endl;
	return 0;
}
