#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
using namespace std;
int main() {
	int n, m;
	int q[100000];
	cin >> n >> m;
	for (int i = 0; i < m; i++) {
		int a, b;
		cin >> a >> b;
		int o;
		//セット数の判定
		if (n % a == 0) {
			o = n / a;
		}

		else {
			o
				= n / a + 1;
		}
		int y=a * b*o ;
		q[i] = y;
	}

	int e = q[0];
	for (int i = 0; i < m;i++) {
		int u = q[i];
		e = min({ e,u });

	}
	cout << e << endl;
}
