#include <iostream>
#include <stdio.h>
#include <limits.h>

int main() {
	unsigned int X;
	std::cin >> X;
	unsigned int kakko = 2;
	for (unsigned int i = 2; i <= X; i++) {
		kakko = kakko * 2;
	}
	kakko = kakko - 1;
	unsigned int aaa = kakko % 1000000007;
	std::cout << aaa << std::endl;
	return 0;
}
