#include "iostream"
#include "string"
#include "vector"
#include "algorithm"

using namespace std;

struct p {
	long long num, cost;
	bool operator<(const p& another) const {
		//メンバ変数であるnum1で比較した結果を
		//この構造体の比較とする
		return cost < another.cost;
	}
};

int main() {
	vector<p> a;
	int n, m;
	cin >> n >> m;
	for (int i = 0; i < n; i++) {
		long long tmp;
		cin >> tmp;
		p p;
		p.cost = tmp;
		p.num = i;
		a.push_back(p);
	}
	sort(a.begin(), a.end());

	int ans = 0;

	for (int i = 0; i < n; i++) {
		int count = 0;
		while (m >= 0 && count <= a[i].num) {
			m -= a[i].cost;
			ans++;
			count++;
		}
	}

	cout << ans - 1 << endl;

	return 0;
}
