#include<iostream>
#include<vector>
#include<string>

int main() {
	int n;
	int m;
	std::cin >> n;
	std::cin >> m;
	int a[60000];
	int b[60000];
	int c;
	bool flug = true;
	for (int i = 0; i < n; i++) {
		std::cin >> a[i];
		b[i] = i+1;
	}
	int max;
	int maxb;
	for (int i = 0; i < n - 1; i++) {
		max = a[i];
		for (int j = i+1; j < n; j++) {
			if (max < a[j]) {
				max = a[j];
				maxb = j;
			}
		}
		c = a[i];
		a[i] = a[maxb];
		a[maxb] = c;
		c = b[i];
		b[i] = b[maxb];
		b[maxb] = c;
	}

	int ans = 0;
	for (int i = n-1; i >= 0; i--) {
		if (m < a[i]) {
			break;
			
		}
		else{
			if (m / a[i] < b[i]) {
				ans += m / a[i];
				m -= a[i] * (m / a[i]);
			}
			else {
				ans += b[i];
				m -= a[i] * b[i];
			}
		}
	}
	std::cout << ans << std::endl;
}
